mapper.select:
select  t_cpz_plat_product.productID, t_cpz_plat_product.productName, t_cpz_plat_product.productCategoryId, t_cpz_plat_product.productType, t_cpz_plat_product.productStatus, t_cpz_plat_product.tradingUnit, t_cpz_plat_product.productColor, t_cpz_plat_product.productDetail, t_cpz_plat_product_pic.picName, t_cpz_plat_product_pic.picNorms, t_cpz_plat_product_pic.picUrl from t_gsp_product_norms_map inner join t_cpz_plat_product on t_gsp_product_norms_map.shopProductId=t_cpz_plat_product.productIDt_cpz_plat_product inner join t_cpz_product_level on t_cpz_plat_product.productCategoryId=t_cpz_product_level.productCategoryIdt_cpz_plat_product_pic inner join t_cpz_plat_product on t_cpz_plat_product_pic.productId=t_cpz_plat_product.productID where 		
 t_cpz_plat_product.productName= #{productName}


 and t_cpz_plat_product.productType= #{productType}
